import React, { Component, PropTypes } from "react";
import axios from "axios";
import { Router, Route, browserHistory, IndexRoute } from "react-router";

import { Switch, Redirect, BrowserRouter } from "react-router-dom";
import Login from "./ApplicantPages/Login";
import ApplicantMenu from "./ApplicantPages/ApplicantMenu";
import ApplicantLeftMenu from "./ApplicantPages/ApplicantLeftMenu";
import { Container, Row, Col } from "reactstrap";
import Applicants from "./Applicants";
import Profile from "./ApplicantPages/Profile";
import Logout from "./Logout";
import Programs from "./CommonPages/Programs";
import Register from "./ApplicantPages/Register";
import Footer from "./ApplicantPages/Footer";
import Notification from "./ApplicantPages/Notification";

import UploadDocuments from "./ApplicantPages/UploadDocuments";
import ChangePassword from "./ApplicantPages/ChangePassword";
import OpenProgram from "./DepartmentPages/OpenProgram";
import EditProgram from "./DepartmentPages/EditProgram";
import Dashboard from "./CommonPages/Dashboard";
import CommonLeftMenu from "./CommonPages/CommonLeftMenu";
import CommonMenu from "./CommonPages/CommonMenu";
import Root from "./Root";

export default class App extends React.Component {
  state = {

    currentPage: "",
    loginPage:false,
    programId: "",
   token:"",
    profile: {
      role: "",
      _id: "",
      name: "",
      surname: "",
      email: "",
      password: "",
      phon: "",
      address: "",
      __v: 0,
    },


  };

  componentWillMount = async () => {
    await this.getProfile;
  };

  changeIsLoggedIn = (LoggedIn) => {
    this.setState({ isLoggedIn: LoggedIn });
  };

  changeProfil = (profil) => {
    this.setState({ currentCategory: profil });
    this.render();
  };


  setPrograms = (programs) => {
    this.setState({ programs: programs });
  };

  setProfile = (aProfile) => {
    this.setState({ profile: aProfile });
  };

  setCurrentPage = (value) => {
    this.setState({
      currentPage: value,
    });
    this.render();
  };

  setToken=(value)=>{
    this.setState({token:value})
  }

setLogin=()=>{
  if(this.state.loginPage){this.setState({loginPage:false})}
  else {this.setState({loginPage:true})}
  this.render();
}

  openLeftMenu = () => {
    switch (this.state.profile.role) {
      case "":
        return (
          <CommonLeftMenu
            setCurrentPage={this.setCurrentPage}
          />
        );
      case "applicant":
        return (
          <ApplicantLeftMenu
          setCurrentPage={this.setCurrentPage}
          />
        );
      case "gradschool":
        return (
          <Profile
            ProfileInfo={this.state.ProfileInfo}
            changeIsLoggedIn={this.changeIsLoggedIn}
            info={this.state.isLoggedIn}
          />
        );
      case "department":
        return (
          <Programs
            setPrograms={this.setPrograms}
            changeCategory={this.changeCategory}
            currentCategory={this.state.currentCategory}
            info={this.state.isLoggedIn}
          />
        );
      case "admin":
        return <ApplicantLeftMenu setCurrentPage={this.state.setCurrentPage}/>;

      default:
        return CommonLeftMenu;
    }
  };








  openMenu = () => {
    switch (this.state.profile.role) {
      case "":
        return (
          <CommonMenu setCurrentPage={this.setCurrentPage} setLogin={this.setLogin}/>

        );
      case "applicant":
        return (
          <ApplicantMenu/>
        );
      case "gradschool":
        return (
          <Profile
            ProfileInfo={this.state.ProfileInfo}
            changeIsLoggedIn={this.changeIsLoggedIn}
            info={this.state.isLoggedIn}
          />
        );
      case "department":
        return (
          <Programs
            setPrograms={this.setPrograms}
            changeCategory={this.changeCategory}
            currentCategory={this.state.currentCategory}
            info={this.state.isLoggedIn}
          />
        );
      case "admin":
        return null;

      default:
        return <CommonMenu setCurrentPage={this.setCurrentPage} setLogin={this.setLogin}/>;
    }
  };








  openCurrentPage = () => {
    switch (this.state.currentPage) {
      case "":
        return <Dashboard />;
      case "Register":
        return <Register />;
      case "Profile":
        return (
          <Profile
            token={this.state.token}
          />
        );
      case "Programs":
        return (
          <Programs
            setPrograms={this.setPrograms}
            changeCategory={this.changeCategory}
            currentCategory={this.state.currentCategory}
            info={this.state.isLoggedIn}
          />
        );

        case "UploadDocuments":
          return (
            <UploadDocuments
              setPrograms={this.setPrograms}
              changeCategory={this.changeCategory}
              currentCategory={this.state.currentCategory}
              info={this.state.isLoggedIn}
            />
          );
      case "EditProgram":
        return (
          <EditProgram
            setProgram={this.setProgram}
            changeCategory={this.changeCategory}
            currentCategory={this.state.currentCategory}
            info={this.state.isLoggedIn}
          />
        );
      case "Logout":
        return <Logout />;
      default:
        return null;
    }
  };


  openLogin=()=>{
    if(this.state.loginPage){

        return <Login setProfile={this.setProfile} setLogin={this.setLogin} setCurrentPage={this.setCurrentPage} setToken={this.setToken}/>         

    }else{
      return null
    }
  }


openPage=()=>{ 
  var page = new Object();
   page.leftmenu= this.openLeftMenu();
    page.menu=this.openMenu();
    page.body= this.openCurrentPage();
    page.login= this.openLogin();
    return page
    }




  render() {

    var page= this.openPage()
    return (


      <Root page={page} />
    );
  }
}
