import React, { Component } from "react";
import "../assets/img/apple-icon.png";
import "../assets/img/favicon.ico";
import { Button, Form, FormGroup, Label, Input, FormText } from "reactstrap";
import axios from "axios";
import ReCAPTCHA from "react-google-recaptcha";

export default class NewRegister extends Component {
  state = {
    firstname: "",
    middlename: "",
    lastname: "",
    password: "",
    email: "",
    program: "",
    phone: "",
    address: "",
    city:"",
    country:"",
    postalCode:"",
    datas: [],
    isVerified: false,
    programs: [],
  };

  componentWillMount() {
    this.jwtHandler();
  }
  componentDidUpdate() {
    console.log(this.state.program);
  }

  handlechange = (event) => {
    const target = event.target;
    const name = target.name;
    const value = target.value;

    this.setState({
      [name]: value,
    });
  };

  recaptchaLoaded() {
    console.log("reCAPTHA loaded successfuly");
  }

  jwtHandler = async () => {
    await axios({
      url: "http://commerchant.herokuapp.com/programs",
      method: "GET",
    })
      .then((response) =>
        this.setState({ programs: response.data.payload.programs })
      )
      .catch(() => console.log("Programlar alınamadı"));
  };

  change = (event) => {
    this.setState({ programs: event.target.value });
  };

  checkforVerification = (event) => {
    if (this.isVerified) {
      this.submit(event);
    }
  };

  submit = async (event) => {
    event.preventDefault();

    const payload = {
      firstname: this.state.firstname,
      middlename: this.state.middlename,
      lastname: this.state.lastname,
      email: this.state.email,
      password: this.state.password,
      phone: this.state.phone,
      address: this.state.address,
    };

    await axios({
      url: "http://commerchant.herokuapp.com/accounts/register",
      method: "POST",
      data: payload,
    })
      .then(
        (response) => (
          this.setState({ datas: response }), alert(response.data.message)
        )
      )
      .catch(() => console.log("Register Başarısız"));
  };

  onloadCallback = function () {
    this.setState({ isVerified: true });
  };
  render() {
    return (
      <div>
        <div>
          <div class="card" border-style="double">
            <div class="card-header">
              <h4 class="card-title">Create Account</h4>
            </div>
            <div class="card-body">
              <form>
                <div class="row">
                  <div class="col-md-4 pr-1">
                    <div class="form-group">
                      <Label for="name">First Name</Label>
                      <Input
                        type="text"
                        name="firstname"
                        value={this.state.name}
                        placeholder="Enter Your First Name!"
                        onChange={this.handlechange}
                      />
                    </div>
                  </div>
                  <div class="col-md-4 pr-1">
                    <div class="form-group">
                      <Label for="middlename">Middle Name</Label>
                      <Input
                        type="text"
                        name="middlename"
                        value={this.state.username}
                        placeholder="Enter Your Middle Name!"
                        onChange={this.handlechange}
                      />
                    </div>
                  </div>
                  <div class="col-md-4 pl-1">
                    <div class="form-group">
                      <Label for="lastname">Last Name</Label>
                      <Input
                        type="text"
                        name="lastname"
                        value={this.state.username}
                        placeholder="Enter Your Last Name!"
                        onChange={this.handlechange}
                      />
                    </div>
                  </div>
                </div>
                <div class="row">
                  <div class="col-md-6 pr-1">
                    <div class="form-group">
                      <label for="exampleFormControlSelect1">
                        Program to apply
                      </label>

                      <select
                        class="form-control"
                        id="exampleFormControlSelect1"
                      >
                        {" "}
                        {this.state.programs.map((aProgram) => (
                          <option>{aProgram.name}</option>
                        ))}
                        ;
                      </select>
                    </div>
                  </div>
                  <div class="col-md-6 pl-1">
                    <div class="form-group">
                      <Label for="email">Email</Label>
                      <Input
                        type="email"
                        name="email"
                        value={this.state.email}
                        placeholder="Enter your e-mail!"
                        onChange={this.handlechange}
                      />
                    </div>
                  </div>
                </div>
                <div class="row">
                  <div class="col-md-6 pr-1">
                    <div class="form-group">
                      <label for="exampleInputEmail1">Password</label>
                      <input
                        type="password"
                        class="form-control"
                        id="inputPassword"
                        placeholder="Password"
                      />
                    </div>
                  </div>
                  <div class="col-md-6 pl-1">
                    <div class="form-group">
                      <label for="exampleInputEmail1">Validate Password</label>
                      <input
                        type="password"
                        class="form-control"
                        id="inputPassword"
                        placeholder="Password"
                      />
                    </div>
                  </div>
                </div>
                <div class="row">
                  <div class="col-md-12">
                    <div class="form-group">
                      <Label for="address">Address</Label>
                      <Input
                        type="text"
                        name="address"
                        value={this.state.contact}
                        placeholder="Enter Your Address!"
                        onChange={this.handlechange}
                      />
                    </div>
                  </div>
                </div>
                <div class="row">
                  <div class="col-md-4 pr-1">
                    <div class="form-group">
                      <label>City</label>
                      <input
                        type="text"
                        class="form-control"
                        placeholder="City"
                        value="City"
                      />
                    </div>
                  </div>
                  <div class="col-md-4 px-1">
                    <div class="form-group">
                      <label>Country</label>
                      <input
                        type="text"
                        class="form-control"
                        placeholder="Country"
                        value="Turkey"
                      />
                    </div>
                  </div>
                  <div class="col-md-4 pl-1">
                    <div class="form-group">
                      <label>Postal Code</label>
                      <input
                        type="number"
                        class="form-control"
                        placeholder="ZIP Code"
                        value="Postal Code"
                      />
                    </div>
                  </div>
                </div>
                <div class="row">
                  <div class="col-md-12">
                    <div class="form-group">
