import React, { Component } from "react";
import axios from "axios";

export default class UploadDocuments extends Component {
  state = {
    titleField : { title : "" ,},
    documentField : { document: ""},
    formFields: { }
  };

  upload = async (event) => {
    const target = event.target;
    const name = target.name;
    const value= target.value;
    var  fileInput = event.target.files[0];
    var data = new FormData();


    data.append('title', name);
    data.append('document', fileInput, value);

    var formData= {
      title: 'name',
      document:{
        value: fileInput,
        options : {
          filename: 'value',
          contentType: null
        }
      }
    }


    await axios({

      url: "http://commerchant.herokuapp.com/documents/upload/",

      method: "POST",
      headers: {
        //   Authorization: window.localStorage.getItem("token"),
        Authorization:"Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..RQgtpcBfTpJ59UlNUla_sGe7gJpsuFn4mb8snSoZyk0"
       },
      body: formData,
      redirect: 'follow'
    })
      .then((response) => console.log(response.success))
      .catch(() => console.log("Upload Başarısız"));
  };

  render() {
    return (
      <div className="wrapper">
        <div className="content">
          <div className="container-fluid">
            <div className="row">
              <div className="col-md-8">
                <div className="card">
                  <div className="card-header">
                  <h4> Step 1 / 4</h4>
                    <h4 className="card-title">
                      <center>Add Documents</center>
                    </h4>
                  </div>

                  <div className="card-body">
                    <div className="table-full-width">
                      <table className="table">
                        <tbody>
                          <tr>
                            <td className="col-md-8">
                              <p>Photo</p>
                            </td>

                            <td>
                              <form>
                                <div className="form-group rounded">

                                  <label

                                    className="custom-file-upload"
                                  >
                                    Choose a file
                                  </label>
                                  <input
                                    type="file"
                                    name="photo"
                                    onChange={this.upload}
                                  />
                                </div>
                              </form>
                            </td>
                          </tr>

                          <tr>
                            <td>
                              <p>Undergrad Transcript</p>
                            </td>

                            <td>
                              <form>
                                <div className="form-group rounded">
                                  <label
                                    for="file-upload"
                                    className="custom-file-upload"
                                  >
                                    Choose a file
                                  </label>
                                  <input

                                    type="file"
                                    name="underTrans"
                                    onChange={this.upload}
                                  />
                                </div>
                              </form>
                            </td>
                          </tr>
                        </tbody>
                      </table>
                    </div>
                  </div>

                  <div className="card-footer">
                    <div className="row float-right">
                      <button type="button" className="btn btn-primary btn-block">
                        Next Step
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
