import React, { Component } from "react";
import axios from "axios";
import moment from 'moment';

export default class Notification extends Component {
  state = {
    nots: [],
    curTime: "",
    selectedNotification: "",
    title: "",
    note:"",
    from:"",
    date:""

  };

  takeNotifications = async () => {
    await axios({
      url: "http://commerchant.herokuapp.com/notifications/",
      method: "GET",
      headers: {
        //   Authorization: window.localStorage.getItem("token"),
        Authorization:
        "Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..-hxmQVkSYBZmyI4jbuxDCXu_hdO4WbIZuFi2U2Ar7N8"      },
    })
      .then((response) =>
         (this.setState({ nots: response.data.payload.notifications }),
         this.setState({ title: response.data.payload.notifications[0].title }),
         this.setState({ note: response.data.payload.notifications[0].note }),
         this.setState({ from: response.data.payload.notifications[0].from }),
         this.setState({ date: response.data.payload.notifications[0].date })
         )
      )
      .catch(() => console.log("Profil gÃ¶sterilemedi"));
  };


  selectANot = (aNotification) => {
    this.setState({ selectedNotification: aNotification });
    this.setState({ title: aNotification.title });
    this.setState({ note: aNotification.note });
    this.setState({ from: aNotification.from });
    this.setState({ date: aNotification.date })
  };



  deleteNot = async (id) =>{

    await axios({

        url: "http://commerchant.herokuapp.com/notifications/"+id,
        method: "DELETE",
        headers: {
            Authorization: window.localStorage.getItem("token"),
          },
      })
        .then((response) =>
           (this.setState({ nots: response.data.payload.notifications })
           )
        )
        .catch(() => console.log("Notifications are Couldn't be shown"));
  }

  showSelectedNote = () => {
    try{
      return (
        <div className="col-md-8">
          <div className="card">
            <div className="card-header">
              <h4 className="card-title">
                {this.state.title}
              </h4>
              <p className="card-category">
                {this.state.from}
              </p>
            </div>
            <div className="card-body">
              <div className="typography-line">
                <p>{this.state.note}</p>
              </div>
            </div>
            <div className="card-body">
              <div className="typography-line">
                <p>{moment(this.state.date).format("dddd, MMM DD,  HH:mm ")}</p>

              </div>
            </div>
          </div>
        </div>
      );

}catch(e){
    if(e){
        return ""
    }
}
  };



  componentWillMount=async()=> {
    await this.takeNotifications();
    this.setState({ curTime: new Date().toLocaleString() });

  }

  render() {

    return (
      <div className="wrapper">
        <div className="content">
          <div className="container-fluid">
            <div className="row">
              <div className="col-md-4">
                <div className="card  card-tasks">
                  <div className="card-header ">
                    <h4 className="card-title">Notifications</h4>
                    <a href="#" ><p className="card-title" onClick={() => window.location.reload(false)}>Refresh</p></a>
                  </div>
                  <div className="card-body ">
                    <div className="table-full-width">
                      <table className="table">
                        <tbody>
                          {this.state.nots.map((aNotification) => (
                            <tr  onClick={() => this.selectANot(aNotification)}
                                key={aNotification.title}
                            >
                              <td>
                                <div >
                                  <label className="form-check-label">
                                    <input
                                      type="checkbox"
                                      value=""
                                        // checked="true"
                                    />
                                    <span className="form-check-sign"></span>
                                  </label>
                                </div>
                              </td>
                              <td>{aNotification.title}</td>
                              <td className="td-actions text-right">
                                <button
                                  type="button"
                                  rel="tooltip"
                                  title="delete"
                                  className="btn btn-danger btn-simple btn-link"
                                  data-original-title="Remove"
                                  onClick={() => this.deleteNot(aNotification._id)}
                                >
                                  <p ></p>
                                </button>
                              </td>
                            </tr>
                          ))}
                        </tbody>
                      </table>
                    </div>
                  </div>
                  <div className="card-footer ">
                    <hr />
                    <div className="stats">
                      <i className="now-ui-icons loader_refresh spin"></i> Updated
                      at {this.state.curTime}

                    </div>
                  </div>
                </div>
              </div>

              {this.showSelectedNote()}
            </div>
          </div>
        </div>
      </div>
    );
  }
}
