import React, { Component } from "react";
import { ListGroup, ListGroupItem } from "reactstrap";

export default class LeftMenu extends Component {
  state = {
    menuElements: [{ CategoryName: "Login" }, { CategoryName: "Register" }],
    currentCategory: "",
  };

  render() {
    return (
      <div>
        <h3>{this.props.info.title}</h3>
        <ListGroup>
          {this.state.menuElements.map((category) => (
            <ListGroupItem onClick={()=> this.setState({currentCategory : category.CategoryName})} key={category.CategoryName}>
              {category.CategoryName}
            </ListGroupItem>
          ))}
        </ListGroup>
          <h3>{this.state.currentCategory}</h3>
      </div>
    );
  }
}
