import React, { Component } from "react";
import axios from "axios";

export default class OpenProgram extends Component {
  state = {
    coordinators:[],
    coordinators:[],
    departments: [
      "Department of Physics",
      "Department of Photonics",
      "Department of Chemistry",
      "Department of Mathematics",
      "Department of Moleculer Biology and Genetics",
      "Department of Bioengineering",
      "department of Chemical Engineering",
      "Department of CIVIL ENGINEERING",
      "Department of CIVIL ENGINEERING ",
      "DEPARTMENT OF ELECTRICAL AND ELECTRONICS ENGINEERING",
      "DEPARTMENT OF ENERGY SYSTEMS ENGINEERING",
      "DEPARTMENT OF ENVIROMENTAL ENGINEERING",
      "Department of FOOD ENGINEERING",
      "Department of DEPARTMENT OF MATERIALS SCIENCE AND ENGINEERING",
      "DEPARTMENT OF MECHANICAL ENGINEERING",
      "DEPARTMENT OF ARCHITECTURE",
      "DEPARTMENT OF CITY AND REGIONAL PLANNING",
      "DEPARTMENT OF INDUSTRIAL DESIGN",
      "DEPARTMENT OF ARCHITECTURAL RESTORATION",
    ],
    name: null,
    department: [],
    degree: null,
    quota:0,
    description:"",
    alesRequirement:false,
    sgkRequirement:false,
    masterRequirement:false,
  };

  handleCoordinator = (event) => {
    const target = event.target;
    const name = target.name;
    const id = target._id;

    this.setState({
      [name]: id,
    });
  };

  handlechange = (event) => {
    const target = event.target;
    const name = target.name;
    const value = target.value;

    this.setState({
      [name]: value,
    });
  };
  handleRequirements = (event)=>{
    const target = event.target;
    const name = target.name;
    if(target.checked){
        this.setState({
            [name]: true,
          });
    }else{
        this.setState({
            [name]: false,
          });
    }
  }


   removeElement=(array, value)=> {
    var what, a = array, L = a.length, ax;
    while (L > 1 && array.length) {
        what = a[--L];
        while ((ax= array.indexOf(what)) !== -1) {
            array.splice(ax, 1);
        }
    }
    return array;
}


handleDepartment =(event)=>{
    const target = event.target;
    if(target.checked){

            this.setState(prevState => ({
                program: [...prevState.program, target]
              }))

    }else{
        this.setState({
            program: this.removeElement(this.state.program, target)
          });
    }

}




componentWillMount() {
    this.jwtHandler();
  }








  submit = async (event) => {
    event.preventDefault();




    const payload = {
        coordinator: this.state.coordinator,
        name: this.state.name,
        department: this.state.department,
        degree: this.state.degree,
        quota: this.state.quota,
        description: this.state.description,
        alesRequirement: this.state.alesRequirement,
        sgkRequirement: this.state.sgkRequirement,
        masterRequirement: this.state.masterRequirement,
    };

    await axios({
      url: "http://commerchant.herokuapp.com/programs",
      method: "POST",
      data: payload,
    })
      .then(
        (response) => (
          this.setState({ datas: response }), alert(response.data.message)
        )
      )
      .catch(() => console.log("Register Başarısız"));
  };

  render() {
    return (
      <div classNameName="wrapper">
        <div className="content">
          <div className="container-fluid">
            <div className="row">
              <div className="col-md-8">
                <div className="card">
                  <div className="card-header">
                    <h4 className="card-title">New Program</h4>
                  </div>
                  <div className="card-body">
                    <form onSubmit={this.submit}>
                      <div className="row">
                        <div className="col-md-8 pr-1">
                          <div className="form-group">
                            <label>Program Name</label>
                            <input
                              name="name"
                              onChange={this.handleChange}
                              type="text"
                              className="form-control"
                              placeholder="Program Name"
                            />
                          </div>
                        </div>
                        <div className="col-md-4 pr-1">
                          <div className="form-group">
                            <label>Quota</label>
                            <input
                              type="number"
                              name="quota"
                              onChange={this.handleChange}
                              className="form-control"
                              placeholder="Quota"
                              min="1"
                              max="100"
                            />
                          </div>
                        </div>
                        <div className="col-md-4">
                          <div className="form-group">
                            <label>Announce Date</label>
                            <input
                              type="date"
                              name="announceDate"
                              onChange={this.handleChange}
                              className="form-control"
                            />
                          </div>
                        </div>

                        <div className="col-md-4">
                          <div className="form-group">
                            <label>Application Deadline Date</label>
                            <input
                              type="date"
                              name="applicationDeadline"
                              onChange={this.handleChange}
                              className="form-control"
                            />
                          </div>
                        </div>

                        <div className="col-md-8 pr-1">
                          <div className="form-group">
                            <label for="coordinator">Coordinator</label>

                            <select
                              name="coordinator"
                              className="form-control"
                              id="vcoordinator"
                              onChange={this.handleCoordinator}
                            >
                              {this.state.coordinators.map((aCoordinator) => (
                                <option key={aCoordinator.email}>
                                  {aCoordinator.name} {aCoordinator.surname}
                                </option>
                              ))}
                              ;
                            </select>
                          </div>
                        </div>
                      </div>


                      <div class="row">
                        <div class="col-md-12 pr-1">
                          <label>Departments</label>


                          {this.state.departments.map((aDepartment) => (
                              <div>  
                             <input
                             type="checkbox"
                             id="sc1"
                             name="sc1"
                             value="Physics"
                             onChange={this.handleDepartment}
                           />
                           <label for="sc1"> {aDepartment}</label>
                           <br />

                           </div>

                            ))}



                        </div>
                      </div>














                      <div className="row">
                        <div className="col-md-4pr-1">
                          <label>Degree</label>
                          <div className="form-group">
                            <select
                              className="form-control"
                              id="exampleFormControlSelect1"
                              name="degree"
                              onChange={this.handleChange}
                            >
                              <option value="master">Master</option>
                              <option value="doctorate">Doctorate</option>
                            </select>
                          </div>
                        </div>
                      </div>

