import React, { Component } from "react";
import Login from "./Login";
import Menu from "./Menu";
import LeftMenu from "./LeftMenu";
import { Container, Row, Col } from "reactstrap";
import Applicants from "./Applicants";

export default class App extends Component {

  state={currentCategory: ""}

  changeCategory = (category) => {
    this.setState({ currentCategory: category.CategoryName });
  };

  render() {
    let leftMenuInfo = { title: "LeftMenu" };
    let menuInfo = { title: "Menu" };
    let loginInfo = { title: "Login" };
    let applicantInfo = { title: "Applicants" };
    return (
      <div>
        <Container>
          <Row>
            <Menu info={menuInfo}></Menu>
          </Row>
          <Row>
            <Col xs="3">
              <LeftMenu currentCategory={this.state.currentCategory} changeCategory={this.changeCategory} info={leftMenuInfo}></LeftMenu>
            </Col>
            <Col xs="9">
              if(currentCategory="Login"){
                <Login currentCategory={this.state.currentCategory} info={loginInfo}></Login>
              }else{

              }

            </Col>
          </Row>
          {/* <Row>
            <Applicants info={applicantInfo}></Applicants>
          </Row> */}
        </Container>
      </div>
    );
  }
}
