import React, { Component, PropTypes } from "react";
import axios from "axios";
import { Router, Route, browserHistory, IndexRoute } from "react-router";

import { Switch, Redirect, BrowserRouter } from "react-router-dom";
import Login from "./ApplicantPages/Login";
import ApplicantMenu from "./ApplicantPages/ApplicantMenu";
import ApplicantLeftMenu from "./ApplicantPages/ApplicantLeftMenu";
import { Container, Row, Col } from "reactstrap";
import Applicants from "./Applicants";
import Profile from "./Profile";
// import Profile from "./Profile";
import Logout from "./Logout";
import Programs from "./CommonPages/Programs";

import Register from "./ApplicantPages/Register";
import Footer from "./ApplicantPages/Footer";
import Notification from "./ApplicantPages/Notification";
import EditProfile from "./ApplicantPages/EditProfile";

import UploadDocuments from "./ApplicantPages/UploadDocuments";
import ChangePassword from "./ApplicantPages/ChangePassword";
import OpenProgram from "./DepartmentPages/OpenProgram";
import EditProgram from "./DepartmentPages/EditProgram";
import Dashboard from "./CommonPages/Dashboard";
import CommonLeftMenu from "./CommonPages/CommonLeftMenu";
import CommonMenu from "./CommonPages/CommonMenu";
import Root from "./Root";

export default class App extends React.Component {
  state = {
    loadPage: "",
    currentPage: "",
    leftMenuInfo: { title: "LeftMenu" },
    menuInfo: { title: "IZTECH GRADUATE PROGRAM" },
    loginInfo: { title: "Login" },
    applicantInfo: { title: "Applicants" },
    ProfileInfo: { title: "Profile" },
    ProgramsInfo: { title: "Programs" },
    programs: [],
    programId: "5ee4098d200e5400045d0d1d",
    token: "",
    profile: {
      role: "",
      _id: "",
      name: "",
      surname: "",
      email: "",
      password: "",
      phon: "",
      address: "",
      __v: 0,
    },

    page:{
    leftmenu: (
      <CommonLeftMenu/>
    ),

    menu: <CommonMenu/>,

    body:<Dashboard />,
    login: <Login/>,
    }
  };

  componentWillMount = async () => {
    await this.getProfile;
  };

  changeIsLoggedIn = (LoggedIn) => {
    this.setState({ isLoggedIn: LoggedIn });
  };

  changeProfil = (profil) => {
    this.setState({ currentCategory: profil });
    this.render();
  };

  setToken = (aToken) => {
    this.setState({ token: aToken });
  };
  setPrograms = (programs) => {
    this.setState({ programs: programs });
  };

  setProfile = (aProfile) => {
    this.setState({ profile: aProfile });
  };

  setCurrentPage = (event) => {
    this.setState({
      currentPage: event.target.value,
    });
    this.render();
  };

  getProfile = async () => {
    await axios({
      url: "http://commerchant.herokuapp.com/accounts/profile",
      method: "GET",
      headers: {
        Authorization: window.localStorage.getItem("token"),
      },
    })
      .then((response) =>
        this.setState({ profile: response.data.payload.account })
      )
      .catch(() => console.log("Profil gösterilemedi"));
    console.log(this.state.myProfile);
  };

  openLeftMenu = () => {
    switch (this.state.profile.role) {
      case "":
        return (
          <CommonLeftMenu
            changeIsLoggedIn={this.changeIsLoggedIn}
            info={this.state.isLoggedIn}
          />
        );
      case "applicant":
        return (
          <ApplicantLeftMenu
            currentCategory={this.state.currentCategory}
            info={this.state.loginInfo}
          />
        );
      case "gradschool":
        return (
          <Profile
            ProfileInfo={this.state.ProfileInfo}
            changeIsLoggedIn={this.changeIsLoggedIn}
            info={this.state.isLoggedIn}
          />
        );
      case "department":
        return (
          <Programs
            setPrograms={this.setPrograms}
            changeCategory={this.changeCategory}
            currentCategory={this.state.currentCategory}
            info={this.state.isLoggedIn}
          />
        );
      case "admin":
        return <ApplicantLeftMenu />;

      default:
        return CommonLeftMenu;
    }
  };

  openCurrentPage = () => {
    switch (this.state.loadPage) {
      case "":
        return <Dashboard />;
      case "Register":
        return <Register />;
      case "Profile":
        return (
          <Profile
            ProfileInfo={this.state.ProfileInfo}
            changeIsLoggedIn={this.changeIsLoggedIn}
            info={this.state.isLoggedIn}
          />
        );
      case "Programs":
        return (
          <Programs
            setPrograms={this.setPrograms}
            changeCategory={this.changeCategory}
            currentCategory={this.state.currentCategory}
            info={this.state.isLoggedIn}
          />
        );
      case "EditProgram":
        return (
          <EditProgram
            setProgram={this.setProgram}
            changeCategory={this.changeCategory}
            currentCategory={this.state.currentCategory}
            info={this.state.isLoggedIn}
          />
        );
      case "Logout":
        return <Logout />;
      default:
        return null;
    }
  };

  componentDidMount() {
    console.log(this.state.token);
  }

  getLogin = () => {
    return <Login setToken={this.setToken} />;
  };

  getAccount = () => {
    return <EditProfile token={this.state.token} />;
  };

  getRoot = () => {
    return <Root token={this.setToken} />;
  };
  render() {
    return (
      // <Router history={browserHistory}>
      //   <Route path="/" component={Root}>
      //     <IndexRoute component={{menu: CommonMenu, leftmenu: this.openLeftMenu,  body:Dashboard}}/>
      //     <Route path="home" component={{menu: CommonMenu, leftmenu: this.openLeftMenu,  body:Dashboard}}></Route>
      //     <Route path="login"  component={{login :this.getLogin    ,menu: CommonMenu, leftmenu: this.openLeftMenu,  body:Register}}></Route>
      //     <Route path="programs" component={{menu: CommonMenu, leftmenu: this.openLeftMenu,  body:Programs}}></Route>
      //     <Route path="register" component={Register}></Route>
      //     </Route>

      //     <Route path="/applicant" component={Root}>
      //     <Route path="home" component={{menu: ApplicantMenu, leftmenu: ApplicantLeftMenu,  body:Programs}}></Route>
      //     <Route path="account" component={{menu: ApplicantMenu, leftmenu: ApplicantLeftMenu,  body:this.getAccount}}></Route>
      //     <Route path="uploaddocuments" component={{menu: ApplicantMenu, leftmenu: ApplicantLeftMenu,  body:UploadDocuments}}></Route>

      //     </Route>

      // </Router>

      <Root page={this.state.page}/>
    );
  }
}

