import React, { Component } from "react";
import { Button, Form, FormGroup, Label, Input } from "reactstrap";
import axios from "axios";

export default class Login extends Component {
  constructor(props) {
    super(props);
    this.state = {
      username: "",
      password: "",
      datas: { success: "", token: "" },
      token: "",
      profile: {},
    };
  }

  jwtHandler = async () => {
    await axios({
      url: "http://commerchant.herokuapp.com/users/profile",
      method: "GET",
      headers: new Headers({
        Authorization: localStorage.getItem("token")
      }),
    })
      .then((response) => console.log(response))
      .catch(() => console.log("Can’t access response. Blocked by browser?"));

  };

  submit = async (event) => {
    event.preventDefault();

    const payload = {
      username: this.state.username,
      password: this.state.password,
    };

    await axios({
      url: "http://commerchant.herokuapp.com/users/login",
      method: "POST",
      data: payload,
    })
      .then((data) => this.setState({ datas: data }))

      .catch(() => console.log("Can’t access response. Blocked by browser?"));
    this.state.token = this.state.datas.data.token;

    await window.localStorage.setItem('token', this.token);
    console.log(window.localStorage.getItem('token'));
    //this.jwtHandler();
  };

  handlechange = (event) => {
    const target = event.target;
    const name = target.name;
    const value = target.value;

    this.setState({
      [name]: value,
    });
  };

  render() {
    return (
      <div>
        <h3>{this.props.currentCategory}</h3>
        <Form onSubmit={this.submit}>
          <FormGroup>
            <Label htmlFor="username">Username</Label>
            <Input
              type="text"
              value={this.state.username}
              name="username"
              placeholder="Enter Your Username!"
              onChange={this.handlechange}
            />
          </FormGroup>
          <FormGroup>
            <Label htmlFor="password">Password</Label>
            <Input
              type="password"
              value={this.state.password}
              name="password"
              placeholder="Enter Your Password!"
              onChange={this.handlechange}
            />
          </FormGroup>
          <Button>Submit</Button>
        </Form>
      </div>
    );
  }
}
