import React, { Component } from "react";
import "../assets/img/apple-icon.png";
import "../assets/img/favicon.ico";
import { Button, Form, FormGroup, Label, Input, FormText } from "reactstrap";
import axios from "axios";
import ReCAPTCHA from "react-google-recaptcha";

export default class NewRegister extends Component {
  state = {
    firstname: "",
    middlename: "",
    lastname: "",
    password: "",
    email: "",
    program: "",
    phone: "",
    address: "",
    city: "",
    country: "",
    postalCode: "",
    aboutMe: "",
    isVerified: false,
    programs: [],
    password2:""
  };

  componentWillMount() {
    this.jwtHandler();
  }
  componentDidUpdate() {
    console.log(this.state.program);
  }

  handlechange = (event) => {
    const target = event.target;
    const name = target.name;
    const value = target.value;

    this.setState({
      [name]: value,
    });
  };

  handlePaaword = (event) => {
    const target = event.target;
    const name = target.name;
    const value = target.value;

    this.setState({
      [name]: value,
    });
  };

  handleProgram= (event) =>{
    const target = event.target;
    const name = target.name;
    const id = target._id;

    this.setState({
      [name]: id,
    });
  }


  recaptchaLoaded() {
    console.log("reCAPTHA loaded successfuly");
  }

  jwtHandler = async () => {
    await axios({
      url: "http://commerchant.herokuapp.com/programs",
      method: "GET",
    })
      .then((response) =>
        this.setState({ programs: response.data.payload.programs })
      )
      .catch(() => console.log("Programlar alınamadı"));
  };

  change = (event) => {
    this.setState({ programs: event.target.value });
  };

  checkforVerification = (event) => {
    if (this.isVerified) {
      this.submit(event);
    }
  };

  submit = async (event) => {
    event.preventDefault();

    const payload = {
      firstname: this.state.firstname,
      middlename: this.state.middlename,
      lastname: this.state.lastname,
      email: this.state.email,
      password: this.state.password,
      phone: this.state.phone,
      address: this.state.address,
      program: this.state.program,
    };

    await axios({
      url: "http://commerchant.herokuapp.com/accounts/register",
      method: "POST",
      data: payload,
    })
      .then(
        (response) => (
          this.setState({ datas: response }), alert(response.data.message)
        )
      )
      .catch(() => console.log("Register Başarısız"));
  };

  onloadCallback = function () {
    this.setState({ isVerified: true });
  };

  render() {
    return (
      <div className="wrapper">
        <div>
          <div className="card" border-style="double">
            <div className="card-header">
              <h4 className="card-title">Create Account</h4>
            </div>
            <div className="card-body">
              <form onSubmit={this.submit}>
                <div className="row">
                  <div className="col-md-4 pr-1">
                    <div className="form-group">
                      <Label for="name">Name</Label>
                      <Input
                        type="text"
                        name="name"
                        value={this.state.name}
                        placeholder="Enter Your First Name!"
                        onChange={this.handlechange}
                      />
                    </div>
                  </div>

                  <div className="col-md-4 pl-1">
                    <div className="form-group">
                      <Label for="lastname">Surname</Label>
                      <Input
                        type="text"
                        name="surname"
                        value={this.state.username}
                        placeholder="Enter Your Last Name!"
                        onChange={this.handlechange}
                      />
                    </div>
                  </div>
                </div>
                <div className="row">
                  <div className="col-md-6 pr-1">
                    <div className="form-group">
                      <label for="exampleFormControlSelect1">
                        Program to apply
                      </label>

                      <select

                        className="form-control"
                        id="exampleFormControlSelect1"
                        name="program"
                        onChange={this.handleProgram}
                      >
                        {" "}
                        {this.state.programs.map((aProgram) => (

                          <option key={aProgram._id}>{aProgram.name}</option>
                        ))}
                        ;
                      </select>
                    </div>
                  </div>
                  <div className="col-md-6 pl-1">
                    <div className="form-group">
                      <Label for="email">Email</Label>
                      <Input
                        type="email"
                        name="email"
                        value={this.state.email}
                        placeholder="Enter your e-mail!"
                        onChange={this.handlechange}
                      />
                    </div>
                  </div>
                </div>
                <div className="row">
                  <div className="col-md-6 pr-1">
                    <div className="form-group">
                      <label for="exampleInputEmail1">Password</label>
                      <input
                        type="password"
                        name="password"
                        className="form-control"
                        id="inputPassword"
                        placeholder="Password"
                        onChange={this.handlechange}
                      />
                    </div>
                  </div>
                  <div className="col-md-6 pl-1">
                    <div className="form-group">
                      <label for="exampleInputEmail1">Validate Password</label>
                      <input
                      name="password2"
                        type="password"
                        className="form-control"
                        id="inputPassword"
                        placeholder="Password"
                        onChange={this.handlePaaword}
                      />
                    </div>
                  </div>
                </div>

                <div className="row">
                  <div className="col-md-4 pr-1">
                    <div className="form-group">
                      <Label for="address">City</Label>
                      <Input
                        type="text"
                        name="city"
                        value={this.state.city}
                        placeholder="City!"
                        onChange={this.handlechange}
                      />
                    </div>
                  </div>
                  <div className="col-md-4 px-1">
                    <div className="form-group">
                      <Label for="address">Country</Label>
                      <Input
                        type="text"
                        name="country"
                        value={this.state.country}
                        placeholder="Country"
                        onChange={this.handlechange}
                      />
                    </div>
                  </div>
                  <div className="col-md-4 pl-1">
                    <div className="form-group">
                      <Label for="address">Postal Code</Label>
                      <Input
                        type="numeric"
                        name="postalCode"
                        value={this.state.postalCode}
                        placeholder="Postal Code!"
                        onChange={this.handlechange}
                      />
                    </div>
                  </div>
                </div>
                <div className="row">
                  <div className="col-md-12">
                    <div className="form-group">
                      <Label for="address">Address</Label>
                      <Input
                        type="text"
                        name="address"
                        value={this.state.address}
                        placeholder="Enter Your Address!"
                        onChange={this.handlechange}
                      />
                    </div>
                  </div>
                </div>
                <ReCAPTCHA
                  sitekey="6LeIxAcTAAAAAJcZVRqyHh71UMIEGNQ_MXjiZKhI"
                  render="explicit"
                  onChange={this.onloadCallback}
                />
                <p/>
                <button type="submit" className="btn btn-info btn-fill pull-right">
                  Register
                </button>
                <div className="clearfix"></div>
              </form>
            </div>
          </div>
        </div>
      </div>
