import React, { Component } from "react";
import axios from "axios";
import { Label, Input, } from 'reactstrap';
import moment from "moment";

export default class EditProgram extends Component {
  state = {
      programId: this.props.programId,
      currentProgram: {},
    coordinators: [],
    departments: [
      "Department of Physics",
      "Department of Photonics",
      "Department of Chemistry",
      "Department of Mathematics",
      "Department of Moleculer Biology and Genetics",
      "Department of Bioengineering",
      "department of Chemical Engineering",
      "Department of CIVIL ENGINEERING",
      "Department of CIVIL ENGINEERING ",
      "DEPARTMENT OF ELECTRICAL AND ELECTRONICS ENGINEERING",
      "DEPARTMENT OF ENERGY SYSTEMS ENGINEERING",
      "DEPARTMENT OF ENVIROMENTAL ENGINEERING",
      "Department of FOOD ENGINEERING",
      "Department of DEPARTMENT OF MATERIALS SCIENCE AND ENGINEERING",
      "DEPARTMENT OF MECHANICAL ENGINEERING",
      "DEPARTMENT OF ARCHITECTURE",
      "DEPARTMENT OF CITY AND REGIONAL PLANNING",
      "DEPARTMENT OF INDUSTRIAL DESIGN",
      "DEPARTMENT OF ARCHITECTURAL RESTORATION",
    ],
    name: null,
    department: [],
    degree: null,
    quota: 0,
    description: "",
    alesRequirement: false,
    sgkRequirement: false,
    masterRequirement: false,
    announceDate : "yyyy-mm-dd",
    applicationDeadline: "yyyy-mm-dd",
    checkedDepartments: [],
  };

  handleCoordinator = (event) => {
    const target = event.target;
    const name = target.name;
    const id = target._id;

    this.setState({
      [name]: id,
    });
  };

  handlechange = (event) => {
    const target = event.target;
    const name = target.name;
    const value = target.value;

    this.setState({
      [name]: value,
    });
  };
  handleRequirements = (event) => {
    const target = event.target;
    const name = target.name;
    if (target.checked) {
      this.setState({
        [name]: true,
      });
    } else {
      this.setState({
        [name]: false,
      });
    }
  };

  removeElement = (array, value) => {
    var what,
      a = array,
      L = a.length,
      ax;
    while (L > 1 && array.length) {
      what = a[--L];
      while ((ax = array.indexOf(what)) !== -1) {
        array.splice(ax, 1);
      }
    }
    return array;
  };

  handleDepartment = (event) => {
    const target = event.target;
    if (target.checked) {
      this.setState((prevState) => ({
        checkedDepartments: [...prevState.checkedDepartments, target],
      }));
      event.checked="true"
    } else {
      this.setState({
        checkedDepartments: this.removeElement(this.state.checkedDepartments, target),

      });
      event.checked="false"
    }
  };

infoHandler=()=>{
    this.setState ({name:this.state.program.name,
                    department:this.state.program.department,
                    degree: this.state.program.degree,
                    quota: this.state.program.quota,
                    description: this.state.program.description,
                    alesRequirement: this.state.program.alesRequirement,
                    sgkRequirement: this.state.program.sgkRequirement,
                    masterRequirement: this.state.program.masterRequirement,
                    announceDate:this.state.program.announceDate,
                    applicationDeadline:this.state.program.applicationDeadline,
    })

}




  componentWillMount= async ()=> {
    await this.jwtHandler();
    await this.infoBringer();
    // await this.infoHandler();

