import React, { Component } from "react";
import axios from "axios";

export default class OpenProgram extends Component {
  state = {
    coordinator: [],
    departments: [
      "Department of Physics",
      "Department of Photonics",
      "Department of Chemistry",
      "Department of Mathematics",
      " Department of Moleculer Biology and Genetics",
      "Department of Bioengineering",
      "department of Chemical Engineering",
      " Department of CIVIL ENGINEERING",
      "  Department of CIVIL ENGINEERING ",
      "   DEPARTMENT OF ELECTRICAL AND ELECTRONICS ENGINEERING",
      "DEPARTMENT OF ENERGY SYSTEMS ENGINEERING",
      "DEPARTMENT OF ENVIROMENTAL ENGINEERING",
      "Department of FOOD ENGINEERING",
      " Department of DEPARTMENT OF MATERIALS SCIENCE AND ENGINEERING",
      "DEPARTMENT OF MECHANICAL ENGINEERING",
      "DEPARTMENT OF ARCHITECTURE",
      "DEPARTMENT OF CITY AND REGIONAL PLANNING",
      "DEPARTMENT OF INDUSTRIAL DESIGN",
      "DEPARTMENT OF ARCHITECTURAL RESTORATION",
    ],
  };

  handleCoordinator = (event) => {
    const target = event.target;
    const name = target.name;
    const id = target._id;

    this.setState({
      [name]: id,
    });
  };

  handlechange = (event) => {
    const target = event.target;
    const name = target.name;
    const value = target.value;

    this.setState({
      [name]: value,
    });
  };


  submit = async (event) => {
    event.preventDefault();

    const payload = {
      firstname: this.state.firstname,
      middlename: this.state.middlename,
      lastname: this.state.lastname,
      email: this.state.email,
      password: this.state.password,
      phone: this.state.phone,
      address: this.state.address,
      program: this.state.program,
    };

    await axios({
      url: "http://commerchant.herokuapp.com/accounts/register",
      method: "POST",
      data: payload,
    })
      .then(
        (response) => (
          this.setState({ datas: response }), alert(response.data.message)
        )
      )
      .catch(() => console.log("Register Başarısız"));
  };




  render() {
    return (
      <div classNameName="wrapper">
        <div className="content">
          <div className="container-fluid">
            <div className="row">
              <div className="col-md-8">
                <div className="card">
                  <div className="card-header">
                    <h4 className="card-title">New Program</h4>
                  </div>
                  <div className="card-body">
                    <form  onSubmit={this.submit}>
                      <div className="row">
                        <div className="col-md-8 pr-1">
                          <div className="form-group">
                            <label>Program Name</label>
                            <input
                              name="name"
                              onChange={this.handleChange}
                              type="text"
                              className="form-control"
                              placeholder="Program Name"
                            />
                          </div>
                        </div>
                        <div className="col-md-4 pr-1">
                          <div className="form-group">
                            <label>Quota</label>
                            <input
                              type="number"
                              name="quota"
                              onChange={this.handleChange}
                              className="form-control"
                              placeholder="Quota"
                              min="1"
                              max="100"
                            />
                          </div>
                        </div>
                        <div className="col-md-4">
                          <div className="form-group">
                            <label>Announce Date</label>
                            <input
                              type="date"
                              name="announceDate"
                              onChange={this.handleChange}
                              className="form-control"
                            />
                          </div>
                        </div>

                        <div className="col-md-4">
                          <div className="form-group">
                            <label>Application Deadline Date</label>
                            <input
                              type="date"
                              name="deadlineDate"
                              onChange={this.handleChange}
                              className="form-control"
                            />
                          </div>
                        </div>

                        <div className="col-md-8 pr-1">
                          <div className="form-group">
                            <label for="coordinator">Coordinator</label>

                            <select
                              name="coordinator"
                              className="form-control"
                              id="vcoordinator"
                              onChange={this.handleCoordinator}
                            >
                              {this.state.coordinator.map((aCoordinator) => (
                                <option key={aCoordinator.email}>
                                  {aCoordinator.name} {aCoordinator.surname}
                                </option>
                              ))}
                              ;
                            </select>
                          </div>
                        </div>
                      </div>
                      <div className="row">
                        <div className="col-md-12 pr-1">
                          <label>Departments</label>
                          <div className="form-group">
                            <select
                              className="form-control"
                              id="exampleFormControlSelect1"
                              name="department"
                              onChange={this.handleChange}
                            >
                              {this.state.departments.map((aDepartment) => (
                                <option key={aDepartment.name}>
                                  {aDepartment}
                                </option>
                              ))}
                              ;
                            </select>
                          </div>
                        </div>
                      </div>

                      <button
                        type="submit"
                        className="btn btn-info btn-fill pull-right"
                      >
                        Save Changes
                      </button>
                      <div className="clearfix"></div>
                    </form>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
