import React, { Component } from "react";
import axios from "axios";

import Login from "./ApplicantPages/Login";
import Menu from "./ApplicantPages/Menu";
import LeftMenu from "./ApplicantPages/LeftMenu";
import { Container, Row, Col } from "reactstrap";
import Applicants from "./Applicants";
import Register from "./Register";
// import Profile from "./Profile";
import Logout from "./Logout";
import Programs from "./CommonPages/Programs";

import NewRegister from "./ApplicantPages/NewRegister";
import Footer from "./ApplicantPages/Footer";
import Notification from "./ApplicantPages/Notification";
import Profile from "./ApplicantPages/Profile";

import UploadDocuments from "./ApplicantPages/UploadDocuments";
import ChangePassword from "./ApplicantPages/ChangePassword";
import OpenProgram from "./DepartmentPages/OpenProgram";
import EditProgram from "./DepartmentPages/EditProgram";
import Dashboard from "./CommonPages/Dashboard";
import CommonLeftMenu from "./CommonPages/CommonLeftMenu";
import CommonMenu from "./CommonPages/CommonMenu";

export default class App extends Component {
  state = {

    currentBody: "",
    leftMenuInfo: { title: "LeftMenu" },
    menuInfo: { title: "IZTECH GRADUATE PROGRAM" },
    loginInfo: { title: "Login" },
    applicantInfo: { title: "Applicants" },
    ProfileInfo: { title: "Profile" },
    ProgramsInfo: { title: "Programs" },
    programs: [],
    programId:"5ee4098d200e5400045d0d1d",
    profile:{
      role: "",
      _id:"",
      name: "",
      surname: "",
      email: "",
      password: "",
      phon: "",
      address: "",
      __v: 0
    }

  };





  getProfile= async ()=>{
      await axios({
        url: "http://commerchant.herokuapp.com/accounts/profile",
        method: "GET",
        headers: {
         Authorization: window.localStorage.getItem("token"),
          },
      })
        .then((response) =>
          this.setState({ profile: response.data.payload.account })

        )
        .catch(() => console.log("Profil gösterilemedi"));
        console.log(this.state.myProfile)
 ;
}

componentWillMount=async()=>{
  await this.getProfile
}



openLestMenu = () => {
  switch (this.state.profile.role) {
    case "":
      return (
        <CommonLeftMenu
          changeIsLoggedIn={this.changeIsLoggedIn}
          info={this.state.isLoggedIn}
        />
      );
    case "applicant":
      return (
        <LeftMenu
          currentCategory={this.state.currentCategory}
          info={this.state.loginInfo}
        />
      );
    case "gradschool":
      return (
        <Profile
          ProfileInfo={this.state.ProfileInfo}
          changeIsLoggedIn={this.changeIsLoggedIn}
          info={this.state.isLoggedIn}
        />
      );
    case "department":
      return (
        <Programs
          setPrograms={this.setPrograms}
          changeCategory={this.changeCategory}
          currentCategory={this.state.currentCategory}
          info={this.state.isLoggedIn}
        />
      );
    case "admin":
      return (
        <CommonLeftMenu/>

      );

    default:
      return null;
  }

}





  changeIsLoggedIn = (LoggedIn) => {
    this.setState({ isLoggedIn: LoggedIn });
  };

  changeProfil = (profil) => {
    this.setState({ currentCategory: profil });
    this.render();
  };

  setPrograms = (programs) => {
    this.setState({ programs: programs });
  };

  openCategory = () => {
    switch (this.state.currentCategory) {
      case "Login":
        return (
          <Login
            changeIsLoggedIn={this.changeIsLoggedIn}
            info={this.state.isLoggedIn}
          />
        );
      case "Register":
        return (
          <Register
            currentCategory={this.state.currentCategory}
            info={this.state.loginInfo}
          />
        );
      case "Profile":
        return (
          <Profile
            ProfileInfo={this.state.ProfileInfo}
            changeIsLoggedIn={this.changeIsLoggedIn}
            info={this.state.isLoggedIn}
          />
        );
      case "Programs":
        return (
          <Programs
            setPrograms={this.setPrograms}
            changeCategory={this.changeCategory}
            currentCategory={this.state.currentCategory}
            info={this.state.isLoggedIn}
          />
        );
      case "EditProgram":
        return (
          <EditProgram
            setProgram={this.setProgram}
            changeCategory={this.changeCategory}
            currentCategory={this.state.currentCategory}
            info={this.state.isLoggedIn}
          />
        );
      case "Logout":
        return <Logout />;
      default:
        return null;
    }
  };

  render() {
    return (
      <div className="wrapper">
        {/* <Container>
          <Row>
          <Col xs="3">
              <LeftMenu
                menuElements={this.state.menuElements}
                currentCategory={this.state.currentCategory}
                changeCategory={this.changeCategory}
                showProfile={this.showProfile}
                info={this.state.leftMenuInfo}
              ></LeftMenu>
            </Col>
            <Col xs="9">
            <Row>
            <Menu info={this.state.menuInfo}></Menu>       
            </Row>

            <Row>
                 <NewRegister/>
            </Row>
              </Col>

          </Row>

        </Container> */}
        <div className="row">
          <div className="col-md-2 pr-1">
           { this.openLestMenu()}
          </div>

          <div className="col-md-10 ">
            <div className="row">
              <div className="col-md-12 pr-1">
                {/* <Menu /> */}
                <CommonMenu></CommonMenu>
              </div>
            </div>
            <div className="row">
              <div className="col-md-9 pr-1">
                {/* <NewRegister /> */}

                {/* <Notification/> */}
                {/* <UploadDocuments /> */}
                {/* <Programs/> */}
                {/* <Profile/> */}
                {/* <ChangePassword/> */}
                {/* <OpenProgram/> */}
                {/* <EditProgram programId={this.state.programId}/> */}
        <Dashboard/>

              </div>
  <div className="col-md-3 pr-1">
              <Login ChangeProfile={this.componentWillMount}/>
              </div>
            </div>
            <div className="row">
              <div className="col-md-12 pr-1">
                <Footer />
              </div>
            </div>
          </div>


        </div>
      </div>
    );
  }
}
