import React, { Component } from "react";
import Login from "./Login";
import Menu from "./Menu";
import LeftMenu from "./LeftMenu";
import { Container, Row, Col } from "reactstrap";
import Applicants from "./Applicants";
import Register from "./Register";

export default class App extends Component {

  state={currentCategory: "" , 
  leftMenuInfo : { title: "LeftMenu" },
  menuInfo : { title: "Menu" },
  loginInfo : { title: "Login" },
  applicantInfo : { title: "Applicants" }
}

  changeCategory = (category) => {
    this.setState({ currentCategory: category.CategoryName });
  };




 openCategory = () => {
    switch (this.state.currentCategory) {
      case 'Login':
        return <Login currentCategory={this.state.currentCategory} info={this.state.loginInfo} />;
      case 'Register':
        return <Register currentCategory={this.state.currentCategory} info={this.state.loginInfo} />;
      default:
        return null;
    }
  }

  render() {

    return (
      <div>
        <Container>
          <Row>
            <Menu info={this.state.menuInfo}></Menu>
          </Row>
          <Row>
            <Col xs="3">
              <LeftMenu currentCategory={this.state.currentCategory} changeCategory={this.changeCategory} info={this.state.leftMenuInfo}></LeftMenu>
            </Col>
            <Col xs="9">
              {openCategory()}
            </Col>
          </Row>
          {/* <Row>
            <Applicants info={applicantInfo}></Applicants>
          </Row> */}
        </Container>
      </div>
    );
  }
}
