import React, { Component } from "react";
import { Button, Form, FormGroup, Label, Input, FormText } from "reactstrap";
import axios from "axios";
import ReCAPTCHA from "react-google-recaptcha" 

export default class Register extends Component {

    state = {
      firstName: "",
      lastName: "",
      secondName: "",

      password: "",
      email: "",
      name: "",
      contact: "",
      datas: [],
      isVerified : "false"
    };


  handlechange = (event) => {

      const target = event.target;
      const name = target.name;
      const value = target.value;

      this.setState({
        [name]: value,
      });

  };


recaptchaLoaded(){
  console.log("reCAPTHA loaded successfuly")
}



 checkforVerification = (event) =>{
    if(this.isVerified){
    this.submit(event)
    }
  }

  submit = async (event) => {
    event.preventDefault();


    const payload = {
      username: this.state.username,
      password: this.state.password,
      email: this.state.email,
      name: this.state.name,
      contact: this.state.contact,


    };
    await axios({
      url: "http://commerchant.herokuapp.com/accounts/register",
      method: "POST",
      data: payload,
    })
      .then(
        (response) => (
          this.setState({ datas: response }), alert(response.data.message)
        )
      )
      .catch(() => console.log("Log in Başarısız"));
    };


  onloadCallback = function() {
    this.setState({isVerified : true})
  }

  render() {
    return (
      <div>
        <h3>{this.props.currentCategory}</h3>
        <Form onSubmit={this.submit}>
          <FormGroup>
            <FormGroup>
              <Label for="name">Name</Label>
              <Input
                type="text"
                name="name"
                value={this.state.name}
                placeholder="Enter Your Name!"
                onChange={this.handlechange}
              />
            </FormGroup>

            <Label for="username">Username</Label>
            <Input
              type="text"
              name="username"
              value={this.state.username}
              placeholder="Enter Your Username!"
              onChange={this.handlechange}
            />
          </FormGroup>

          <FormGroup>
            <Label for="email">Email</Label>
            <Input
              type="email"
              name="email"
              value={this.state.email}
              placeholder="Enter your e-mail!"
              onChange={this.handlechange}
            />
          </FormGroup>

          <FormGroup>
            <Label for="examplePassword">Password</Label>
            <Input
              type="password"
              value={this.state.password}
              name="password"
              placeholder="Enter Your Password!"
              onChange={this.handlechange}
            />
          </FormGroup>

          <FormGroup>
            <Label for="contact">Contact</Label>
            <Input
              type="text"
              name="contact"
              value={this.state.contact}
              placeholder="Enter Your Phone Number!"
              onChange={this.handlechange}
            />
          </FormGroup>

          <ReCAPTCHA
            sitekey="6LeIxAcTAAAAAJcZVRqyHh71UMIEGNQ_MXjiZKhI"
            render= "explicit"
            onChange={this.onloadCallback} 
           />
          <Button>Submit</Button>
        </Form>
      </div>
    );
  }
}
