import React, { Component } from "react";
import axios from "axios";

export default class Notification extends Component {
  state = {
    nots: [],
    curTime: "",
    selectedNotification: "",
    title: "",
    note:"",
    from:"",
    date:""

  };

  takeNotifications = async () => {
    await axios({
      url: "http://commerchant.herokuapp.com/notifications/",
      method: "GET",
      headers: {
        //   Authorization: window.localStorage.getItem("token"),
        Authorization:
          "Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..DHzzZRhgqKxdn7SW36tYd2kTBHA8HNxcwoG-QErbH5A",
      },
    })
      .then((response) =>
         (this.setState({ nots: response.data.payload.notifications }),
         this.setState({ title: response.data.payload.notifications[0].title }),
         this.setState({ note: response.data.payload.notifications[0].note }),
         this.setState({ from: response.data.payload.notifications[0].from }),
         this.setState({ date: response.data.payload.notifications[0].date })
        //  this.setState({ dateOne: this.date.split(/[.T]/) }),
        //  console.log(this.dateOne)
         )
      )
      .catch(() => console.log("Profil gösterilemedi"));
  };


  selectANot = (aNotification) => {
    this.setState({ selectedNotification: aNotification });
    this.setState({ title: aNotification.title });
    this.setState({ note: aNotification.note });
    this.setState({ from: aNotification.from });
    this.setState({ date: aNotification.date })
  };

  showSelectedNote = () => {
    try{
      return (
        <div class="col-md-8">
          <div class="card">
            <div class="card-header">
              <h4 class="card-title">
                {this.state.title}
              </h4>
              <p class="card-category">
                {this.state.from}
              </p>
            </div>
            <div class="card-body">
              <div class="typography-line">
                <p>{this.state.note}</p>
              </div>
            </div>
            <div class="card-body">
              <div class="typography-line">
                <p>{this.state.date}</p>
              </div>
            </div>
          </div>
        </div>
      );

}catch(e){
    if(e){
        return ""
    }
}
  };

  componentWillMount() {
    this.takeNotifications();
    this.setState({ curTime: new Date().toLocaleString() });
  }

  render() {
    return (
      <div class="wrapper">
        <div class="content">
          <div class="container-fluid">
            <div class="row">
              <div class="col-md-4">
                <div class="card  card-tasks">
                  <div class="card-header ">
                    <h4 class="card-title">Notifications</h4>
                  </div>
                  <div class="card-body ">
                    <div class="table-full-width">
                      <table class="table">
                        <tbody>
                          {this.state.nots.map((aNotification) => (
                            <tr  onClick={() => this.selectANot(aNotification)}
                                key={aNotification.title}
                            >
                              <td>
                                <div class="form-check">
                                  <label class="form-check-label">
                                    <input
                                      class="form-check-input"
                                      type="checkbox"
                                      value=""
                                    />
                                    <span class="form-check-sign"></span>
                                  </label>
                                </div>
                              </td>
                              <td>{aNotification.title}</td>
                              <td class="td-actions text-right">
                                <button
                                  type="button"
                                  rel="tooltip"
                                  title="delete"
                                  class="btn btn-danger btn-simple btn-link"
                                  data-original-title="Remove"
                                >

                                </button>
                              </td>
                            </tr>
                          ))}
                        </tbody>
                      </table>
                    </div>
                  </div>
                  <div class="card-footer ">
                    <hr />
                    <div class="stats">
                      <i class="now-ui-icons loader_refresh spin"></i> Updated
                      at {this.state.curTime}
                    </div>
                  </div>
                </div>
              </div>

              {this.showSelectedNote()}
            </div>
          </div>
        </div>
      </div>
    );
  }
}
