import React, { Component } from "react";
import axios from "axios";
import { ListGroup, ListGroupItem } from "reactstrap";
import { Table } from "reactstrap";
import ReactDOM from "react-dom";

export default class EditProgram extends Component {
  state = {
    data: {
      alesRequirement: true,
      sgkRequirement: true,
      masterRequirement: true,
      quota: 0,
      _id: "",
      name: "",
      department: "",
      degree: "",
      coordinator: "",
      description: "",
      announceDate: "",
      applicationDeadline: "",
      __v: 0,
    },
    datas: [],
    data: []
  };

  handlechange = (event) => {

    const target = event.target;
    const name = target.name;
    const value = target.value;

    this.setState({
      [name]: value,
    });

};


  componentWillMount() {
    this.jwtHandler();
  }

  jwtHandler = async () => {
    await axios({
      url: "http://commerchant.herokuapp.com/programs",
      method: "GET",
    })
      .then((response) =>
        this.setState({ datas: response.data.payload.programs })
      )
      .catch(() => console.log("Profil gösterilemedi"));
  };




  render() {
    return (
      <div>
        <h2> Edit Program</h2>

        <select id="lang" onChange={this.change} value={this.state.value}>
               {this.state.datas.map((data) => (

                        <option value="select">{data.name}</option>

                ))};
                </select>


      </div>
    );
  }
}
