import React, { Component } from "react";
import axios from "axios";
import { ListGroup, ListGroupItem } from "reactstrap";
import { Table } from 'reactstrap';
import ReactDOM from 'react-dom'

export default class Programs extends Component {
  state = {
    data: {
        alesRequirement: true,
        sgkRequirement: true,
        masterRequirement: true,
        quota: 0,
        _id: "",
        name: "",
        department: "",
        degree: "",
        coordinator: "",
        description: "",
        announceDate: "",
        applicationDeadline: "",
        __v: 0
    },
    datas:[],
    iter:0
  };

  componentWillMount(){

}

componentDidMount(){

}




  jwtHandler = async () => {
    await axios({
      url: "http://commerchant.herokuapp.com/programs",
      method: "GET",
    })
     .then((response) =>  this.setState({ datas:response.data.payload.programs }))
    .catch(() => console.log("Profil gösterilemedi"));
    if(this.state.datas.length = 0){
        this.render();
        this.forceUpdate();
    }

  };



  render() {
    if(this.state.datas.length = 0){
        this.jwtHandler()
      }




    return (

      <div>
        <h2> Programs</h2>

        <Table bordered>
      <thead>
        <tr>
          <th>#</th>
          <th>Program Name</th>
          <th>Quota</th>

        </tr>
      </thead>
      <tbody>
      {this.state.datas.map((data) => ( 
        <tr key={data._id} >
          <th scope="row">{this.iter}</th>
          <td>{data.name}</td>
          <td>{data.quota}</td>
        </tr>
      ))}
      </tbody>
    </Table>
      </div>
    );
  }
}
