import React, { Component } from "react";
import axios from "axios";
import { ListGroup, ListGroupItem } from "reactstrap";
import { Table } from "reactstrap";
import ReactDOM from "react-dom";
import {
  Col,
  Button,
  Form,
  FormGroup,
  Label,
  Input,
  FormText,
} from "reactstrap";

export default class EditProgram extends Component {
  state = {

      alesRequirement: true,
      sgkRequirement: true,
      masterRequirement: true,
      quota: 0,
      _id: "",
      name: "",
      department: "",
      degree: "",
      coordinator: "",
      description: "",
      announceDate: "",
      applicationDeadline: "",
      __v: 0,

    datas: [],

  };

  handlechange = (event) => {
    const target = event.target;
    const name = target.name;
    const value = target.value;

    this.setState({
      [name]: value,
    });
  };

  componentWillMount() {
    this.jwtHandler();
  }

  jwtHandler = async () => {
    await axios({
      url: "http://commerchant.herokuapp.com/programs",
      method: "GET",
    })
      .then((response) =>
        this.setState({ datas: response.data.payload.programs })
      )
      .catch(() => console.log("Profil gösterilemedi"));
  };


  render() {
    return (
      <div>
        <h2> Edit Program</h2>

        <select onChange={this.handlechange} value={this.state.value}>
          {this.state.datas.map((data) => (
            <option value={data} key={data._id}>

              {data.name}

            </option>
          ))}
          ;
        </select>
            <h1>  </h1>
            <h1>  </h1>
        <Form onSubmit={this.submit}>

        <FormGroup check>
            <p>Ales Requirement</p>
            <Label check>
              <Input type="radio" name="radio2" />{' '}
             Yes
            </Label>
          </FormGroup>
          <FormGroup check>
            <Label check>
              <Input type="radio" name="radio2" />{' '}
              No
            </Label>
          </FormGroup>

          <FormGroup check>

            <p>SGK Requirenment</p>
            <Label check>
              <Input type="radio" name="radio2" />{' '}
             Yes
            </Label>
          </FormGroup>
          <FormGroup check>
            <Label check>
              <Input type="radio" name="radio2" />{' '}
              No
            </Label>
          </FormGroup>


          <FormGroup>
            <Label for="middlename">Middle Name</Label>
            <Input
              type="text"
              name="middlename"
              value={this.state.username}
              placeholder="Enter Your Middle Name!"
              onChange={this.handlechange}
            />
          </FormGroup>

          <FormGroup>
            <Label for="lastname">Last Name</Label>
            <Input
              type="text"
              name="lastname"
              value={this.state.username}
              placeholder="Enter Your Last Name!"
              onChange={this.handlechange}
            />
          </FormGroup>

          <FormGroup>
            <Label for="email">Email</Label>
            <Input
              type="email"
              name="email"
              value={this.state.email}
              placeholder="Enter your e-mail!"
              onChange={this.handlechange}
            />
          </FormGroup>

          <FormGroup>
            <Label for="password">Password</Label>
            <Input
              type="password"
              value={this.state.password}
              name="password"
              placeholder="Enter Your Password!"
              onChange={this.handlechange}
            />
          </FormGroup>

          <FormGroup>
            <Label for="phone">Phone</Label>
            <Input
              type="text"
              pattern="[0-9]*"
              name="phone"
              value={this.state.contact}
              placeholder="Enter Your Phone Number!"
              onChange={this.handlechange}
            />
          </FormGroup>

          <FormGroup>
            <Label for="address">Address</Label>
            <Input
              type="text"
              name="address"
              value={this.state.contact}
              placeholder="Enter Your Address!"
              onChange={this.handlechange}
            />
          </FormGroup>

          <Button>Submit</Button>
        </Form>
      </div>
    );
  }
}
