import React, { Component } from "react";
import axios from "axios";
import { Label, Input, } from 'reactstrap';


export default class OpenProgram extends Component {
  state = {
    coordinator:"",
    coordinators:[],
    departments: [
      "Department of Physics",
      "Department of Photonics",
      "Department of Chemistry",
      "Department of Mathematics",
      "Department of Moleculer Biology and Genetics",
      "Department of Bioengineering",
      "department of Chemical Engineering",
      "Department of CIVIL ENGINEERING",
      "Department of CIVIL ENGINEERING ",
      "DEPARTMENT OF ELECTRICAL AND ELECTRONICS ENGINEERING",
      "DEPARTMENT OF ENERGY SYSTEMS ENGINEERING",
      "DEPARTMENT OF ENVIROMENTAL ENGINEERING",
      "Department of FOOD ENGINEERING",
      "Department of DEPARTMENT OF MATERIALS SCIENCE AND ENGINEERING",
      "DEPARTMENT OF MECHANICAL ENGINEERING",
      "DEPARTMENT OF ARCHITECTURE",
      "DEPARTMENT OF CITY AND REGIONAL PLANNING",
      "DEPARTMENT OF INDUSTRIAL DESIGN",
      "DEPARTMENT OF ARCHITECTURAL RESTORATION",
    ],
    name: "",
    departmentArray: [],
    department:"",
    degree: "Master",
    quota:0,
    description:"",
    alesRequirement:false,
    sgkRequirement:false,
    masterRequirement:false,
    // announceDate:null,
    // applicationDeadline:null
  };

  handleCoordinator = (event) => {


    var e = document.getElementById("vcoordinator");
    var input = e.options[e.selectedIndex].value;

    console.log("the input is ", input)


    this.setState({
      coordinator: input,
    });
    console.log("the coordinator is ", this.state.coordinator)
  };




  handlechange = (event) => {
    const target = event.target;
    const name = target.name;
    const value = target.value;

    this.setState({
      [name]: value,
    });
  };

  handleRequirements = (event)=>{
    const target = event.target;
    const name = target.name;
    if(target.checked){
        this.setState({
            [name]: true,
          });
    }else{
        this.setState({
            [name]: false,
          });
    }
  }


   removeElement=(array, value)=> {

    for( var i = 0; i < array.length; i++){ if ( array[i] === value) { array.splice(i, 1); i--; }}

    return array;
}


handleDepartment =(event)=>{
    const target = event.target;
    const temp= this.state.departmentArray;
    if(target.checked){
        temp.push(target.value)
        this.setState({
            departmentArray: temp
    });
    }else{
        this.setState({
            departmentArray: this.removeElement(this.state.departmentArray, target.value)
          });
    }
}




componentWillMount() {
    this.jwtHandler();

  }


  jwtHandler = async () => {
    await axios({
      url: "http://commerchant.herokuapp.com/accounts/all",
      method: "GET",
      headers: {
        // Authorization: window.localStorage.getItem("token"),
        Authorization:
        "Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..nbY9oTz26WWY7r0AEOi-Lf3qMc8hb1w892sNXsv-Tlk"
    },
    })
      .then((response) =>
        this.setState({ coordinators: response.data.payload.accounts.filter(a=> a.role=== "gradschool") })
      )
      .catch(() => console.log("Programlar al覺namad覺"));
  };





  submit = async (event) => {
    event.preventDefault();

    await this.setState({department: this.state.departmentArray.toString()})

    console.log(this.state.coordinator)



    const payload = {
        // coordinator: "5edc34ea000ebe2355921045",
        // name: this.state.name,
        // department: this.state.department.toString(),
        // degree: this.state.degree,
        // quota: this.state.quota,
        // description: this.state.description,
