import React, { Component } from "react";
import Login from "./Login";
import Menu from "./Menu";
import LeftMenu from "./React/LeftMenu";
import { Container, Row, Col } from "reactstrap";
import Applicants from "./Applicants";
import Register from "./Register";
import Profile from "./Profile";
import Logout from "./Logout";
import Programs from "./Programs";
import EditProgram from "./EditProgram";
import NewRegister from "./React/NewRegister";

export default class App extends Component {
  state = {
    currentCategory: "",
    leftMenuInfo: { title: "LeftMenu" },
    menuInfo: { title: "IZTECH GRADUATE PROGRAM" },
    loginInfo: { title: "Login" },
    applicantInfo: { title: "Applicants" },
    ProfileInfo: { title: "Profile" },
    ProgramsInfo: { title: "Programs" },
    programs: [],
    menuElements: [
      { CategoryName: "Register" },
      { CategoryName: "Login" },
      { CategoryName: "Profile" },
      { CategoryName: "Programs" },
      { CategoryName: "Logout" },
    ],
  };

  changeIsLoggedIn = (LoggedIn) => {
    this.setState({ isLoggedIn: LoggedIn });
  };

  changeCategory = (category) => {
    this.setState({ currentCategory: category.CategoryName });
    this.render();
  };

  setPrograms = (programs) => {
    this.setState({ programs: programs });
  };

  openCategory = () => {
    switch (this.state.currentCategory) {
      case "Login":
        return (
          <Login
            changeIsLoggedIn={this.changeIsLoggedIn}
            info={this.state.isLoggedIn}
          />
        );
      case "Register":
        return (
          <Register
            currentCategory={this.state.currentCategory}
            info={this.state.loginInfo}
          />
        );
      case "Profile":
        return (
          <Profile
            ProfileInfo={this.state.ProfileInfo}
            changeIsLoggedIn={this.changeIsLoggedIn}
            info={this.state.isLoggedIn}
          />
        );
      case "Programs":
        return (
          <Programs
            setPrograms={this.setPrograms}
            changeCategory={this.changeCategory}
            currentCategory={this.state.currentCategory}
            info={this.state.isLoggedIn}
          />
        );
      case "EditProgram":
        return (
          <EditProgram
            setProgram={this.setProgram}
            changeCategory={this.changeCategory}
            currentCategory={this.state.currentCategory}
            info={this.state.isLoggedIn}
          />
        );
      case "Logout":
        return <Logout />;
      default:
        return null;
    }
  };

  render() {
    return (
      <div>
        <Container>
          <Row>
           // <Menu info={this.state.menuInfo}></Menu>
          </Row>
          <Row>
            <Col xs="3">
              <LeftMenu
                menuElements={this.state.menuElements}
                currentCategory={this.state.currentCategory}
                changeCategory={this.changeCategory}
                showProfile={this.showProfile}
                info={this.state.leftMenuInfo}
              ></LeftMenu>
            </Col>
            <Col xs="9">
              {/* <div>{this.openCategory()}</div> */}
              <NewRegister programs={this.state.programs} />
            </Col>
          </Row>
        </Container>
      </div>
    );
  }
}
