import React, { Component } from "react";
import { Button, Form, FormGroup, Label, Input, FormText } from "reactstrap";
import { getElementError } from "@testing-library/react";
import axios from 'axios';
import { CookiesProvider } from "react-cookie";

export default class Login extends Component {
  constructor(props) {
    super(props);
    this.state = { username: "", password: "" ,datas: [], token:""};
  }



  jwtHandler = (data) => {

    axios({
        url:"http://commerchant.herokuapp.com/users/login",
        method: "POST", 
        headers: {Authorization: data}
    })

    .then(aToken => this.setState({token: aToken }))
    .catch(() => console.log("Can’t access response. Blocked by browser?"))
    console.log(this.state.token)

};




submit = (event) => {
    event.preventDefault();

    const payload= {
        username : this.state.username,
        password : this.state.password
    };

    axios({
        url:"http://commerchant.herokuapp.com/users/login",
        method: "POST", 
        data: payload
    })

    .then(data => this.setState({datas: data }))
    .catch(() => console.log("Can’t access response. Blocked by browser?"))
    console.log(this.state.datas)
    this.jwtHandler(this.state.datas.data)
};


handlechange= (event) => {
    const target = event.target;
    const name= target.name;
    const value= target.value;

    this.setState({
        [name] : value
    });
};











  render() {

    return (
      <div>
        <h3>{this.props.currentCategory}</h3>
        <Form onSubmit={this.submit}>
          <FormGroup>
            <Label htmlFor="username">Username</Label>
            <Input
              type="text"
              value={this.state.username}
              name="username"
              placeholder="Enter Your Username!"
              onChange = {this.handlechange}
            />
          </FormGroup>
          <FormGroup>
            <Label htmlFor="password">Password</Label>
            <Input
              type="password"
              value={this.state.password}
              name="password"
              placeholder="Enter Your Password!"
              onChange = {this.handlechange}
            />
          </FormGroup>
          <Button>Submit</Button>
        </Form>
      </div>
    );
  }
}
