import React, { Component } from "react";
import Login from "./Login";
import Menu from "./Menu";
import LeftMenu from "./LeftMenu";
import { Container, Row, Col } from "reactstrap";
import Applicants from "./Applicants";
import Register from "./Register";
import Profile from "./Profile";

export default class App extends Component {
  state = {
    currentCategory: "",
    leftMenuInfo: { title: "LeftMenu" },
    menuInfo: { title: "Menu" },
    loginInfo: { title: "Login" },
    applicantInfo: { title: "Applicants" },
    isLoggedIn: false,
  };

  changeIsLoggedIn= (LoggedIn) => {
    this.setState({ isLoggedIn: LoggedIn });
  }

  changeCategory = (category) => {
    this.setState({ currentCategory: category.CategoryName });
  };

  showProfile = ()=>{
    Profile.jwtHandler()
  }
  openCategory = () => {
    switch (this.state.currentCategory) {
      case "Login":
        return (
          <Login
          changeIsLoggedIn={this.changeIsLoggedIn}
          info={this.state.isLoggedIn}
          />
        );
      case "Register":
        return (
          <Register
            currentCategory={this.state.currentCategory}
            info={this.state.loginInfo}
          />
        );
      case "Profile":
        return (
          <Profile
          changeIsLoggedIn={this.changeIsLoggedIn}
          info={this.state.isLoggedIn}
          />
        );
      default:
        return null;
    }
  };

  render() {
    return (
      <div>
        <Container>
          <Row>
            <Menu info={this.state.menuInfo}></Menu>
          </Row>
          <Row>
            <Col xs="3">
              <LeftMenu
                currentCategory={this.state.currentCategory}
                changeCategory={this.changeCategory}

                info={this.state.leftMenuInfo}
              ></LeftMenu>
            </Col>
            <Col xs="9">
              <div>{this.openCategory()}</div>
            </Col>
          </Row>
          {/* <Row>
            <Applicants info={applicantInfo}></Applicants>
          </Row> */}
        </Container>
      </div>
    );
  }
}
