import React, { Component } from "react";
import axios from "axios";

export default class UploadDocuments3 extends Component {
  state = {
    passport: null,
    passportInput: null,
    permissionLetter: null,
    permissionLetterInput: null,
  };

  //If Nationality is "OTHER" then the passport copy is needed from the applicant
  citizenshipHandle = (event) => {
    const target = event.target;
    if (target.value === "Turkish") {
      this.setState({ passportInput: null });
    } else {
      this.setState({
        passportInput: (
          <tr>
            <td>
              <p>Passport</p>
            </td>

            <td>
              <form>
                <div class="form-group rounded">
                  <label for="file-upload" class="custom-file-upload">
                    Choose a file
                  </label>
                  <input
                    type="file"
                    onChange={this.upload}
                    name="passport"
                    id="file-upload"
                  />
                </div>
              </form>
            </td>
          </tr>
        ),
      });
    }
  };

  //If applicant is working the permission letter is needed from the applicant
  workingHandle = (event) => {
    const target = event.target;
    if (target.value === "Yes") {
      this.setState({
        permissionLetterInput: (
          <tr>
            <td>
              <p>Permission Letter</p>
            </td>

            <td>
              <form>
                <div class="form-group rounded">
                  <label for="file-upload" class="custom-file-upload">
                    Choose a file
                  </label>
                  <input
                    type="file"
                    onChange={this.upload}
                    name="permissionLetter"
                    id="file-upload"
                  />
                </div>
              </form>
            </td>
          </tr>
        ),
      });
    } else {
      this.setState({ permissionLetterInput: null });
    }
  };





//Handles Uploading the files
  upload = async (event) => {
    const target = event.target;
    const name = target.name;
    const file = target.files[0];

    await axios({
      url: "http://commerchant.herokuapp.com/documents/upload/",
      method: "POST",
      headers: {
        // Authorization: window.localStorage.getItem("token")
        Authorization:
          "Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..Y9Kt_w2oQkuVHBpcC2jbBK8SzYqXAIPRGuVzKGJtH34",
      },
      body: {
        title: name,
        document: file,
      },
    })
      .then((response) => console.log(response.success))
      .catch(() => console.log("Upload Başarısız"));
  };

  render() {
    return (
      <div class="wrapper">
        <div class="content">
          <div class="container-fluid">
            <div class="row">
              <div class="col-md-8">
                <div class="card">
                  <div class="card-header">
                    <h4> Step 3 / 4</h4>
                    <h4 class="card-title">
                      <center>Citizenship and Working Status</center>
                    </h4>
                  </div>

                  <div class="card-body">
                    <div class="table-full-width">
                      <table class="table">
                        <tbody class="add-remove">
                          <tr class="passport-after">
                            <td class="col-md-8">
                              <p>Nationality</p>
                            </td>

                            <td>
                              <div class="form-check form-check-inline">
                                <input
                                  class="form-check-input"
                                  name="radioOptions1"
                                  type="radio"
                                  id="radio1"
                                  value="Turkish"
                                  onChange={this.citizenshipHandle}
                                />{" "}
                                Turkish
                                <label
                                  class="form-check-label"
                                  for="radio1"
                                ></label>
                              </div>

                              <div class="form-check form-check-inline">
                                <input
                                  class="form-check-input"
                                  name="radioOptions1"
                                  type="radio"
                                  id="radio2"
                                  value="Other"
                                  onChange={this.citizenshipHandle}
                                />{" "}
                                Other
                                <label
                                  class="form-check-label"
                                  for="radio2"
                                ></label>
                              </div>
                            </td>
                          </tr>
                          {this.state.passportInput}
                          <tr>
                            <td>
                              <p>Are you working</p>
                            </td>

                            <td>
                              <div class="form-check form-check-inline">
                                <input
                                  class="form-check-input"
                                  name="radioOptions1"
                                  type="radio"
                                  id="radio1"
                                  value="Yes"
                                  onChange={this.workingHandle}
                                />{" "}
                                Yes
                                <label
                                  class="form-check-label"
                                  for="radio1"
                                ></label>
                              </div>

                              <div class="form-check form-check-inline">
                                <input
                                  class="form-check-input"
                                  name="radioOptions1"
                                  type="radio"
                                  id="radio2"
                                  value="No"
                                  onChange={this.workingHandle}
                                />{" "}
                                No
                                <label
                                  class="form-check-label"
                                  for="radio2"
                                ></label>
                              </div>
                            </td>
                          </tr>
                          {this.state.permissionLetterInput}
                        </tbody>
                      </table>
                    </div>
                  </div>

                  <div class="card-footer">
                    <div class="row float-right">
                      <button type="button" class="btn btn-primary btn-block">
                        Next Step
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
